INSERT INTO `netnews` VALUES (190, '张嘉倪遭吐槽情商低，本人回应争议，却依然不被网友理解', 'http://k.sina.com.cn/article_5330820330_13dbddcea00100l6p8.html?from=ent&subch=star', '虽然被人说是在走真性情路线 但这样的真性情并不能让人喜欢 尤其是在互联网这么发达的今天 张嘉倪这样的表现实在是不能为自己圈粉 甚至送掉了许多因为买超而带来的路人缘 作为一个公众人物', NULL, '2019-04-01 22:48:28', 'sina', NULL, NULL, 0);
INSERT INTO `netnews` VALUES (191, '北京市住建委查处17家房地产经纪机构', 'https://finance.sina.com.cn/roll/2019-04-01/doc-ihtxyzsm2396508.shtml', '已被执法部门立案查处 其在主流互联网平台发布的全部房源信息都被要求下架 其中 四家外省房地产经纪机构在互联网上“跨界”发布本市“小产权房”等违规房源信息 严重扰乱市场秩序 执法部门已通报注册地主管机关重点关注', NULL, '2019-04-01 22:48:21', 'sina', NULL, NULL, 0);
INSERT INTO `netnews` VALUES (192, '贝壳找房CEO彭永东：中国房地产将进入服务者价值时代', 'https://finance.sina.com.cn/roll/2019-04-01/doc-ihsxncvh7456662.shtml', '伴随着居住物理条件改善 消费者品质居住和流通服务需求提升 以及行业与互联网的深度融合 房地产行业将进入“服务者价值”时代 这一阶段 围绕住房的服务比房屋本身更加重要 服务者的角色和价值将被重新定义 服务者要对服务的结果负责', NULL, '2019-04-01 22:48:21', 'sina', NULL, NULL, 0);
