INSERT INTO `netnews` VALUES (206, '中科星图拟冲刺科创板：产品对标谷歌地球 实控人是第一大客户', 'https://finance.sina.com.cn/roll/2019-04-01/doc-ihtxyzsm2406310.shtml', '　　所谓数字地球产品 是指以大数据、云计算和人工智能等新一代信息技术、地理信息技术与航空航天产业深度融合构建的数字化的地球 可以应用于国防、自然资源、交通、气象、海洋、环保、应急等众多领域 　　目前', NULL, '2019-04-01 23:51:38', 'sina', NULL, NULL, 0);
