INSERT INTO `news` VALUES (191, 21, 'ForFuture （2018 9 10） 资讯', '<div class=\"column clearfix\" id=\"col_toparea\" style=\"margin: 25px 0px 26px; padding: 0px; border: 0px; font-size: 16px; vertical-align: baseline; zoom: 1; height: 340px; overflow: hidden; font-family: arial, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei&quot;, 微软雅黑, Tahoma, Helvetica, STHeiti;\">\r\n<div class=\"l-left-col\" style=\"margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; float: left; width: 658px; position: relative;\">\r\n<div class=\"column-title\" style=\"margin: 0px; padding: 0px 0px 9px; border: 0px; vertical-align: baseline; text-align: right;\">\r\n<div class=\"column-title-border\" style=\"margin: 0px; padding: 0px; border-width: 2px 0px 1px; border-top-style: solid; border-right-style: initial; border-bottom-style: solid; border-left-style: initial; border-top-color: rgb(199, 199, 199); border-right-color: initial; border-bottom-color: rgb(236, 236, 236); border-left-color: initial; border-image: initial; vertical-align: baseline; height: 52px; line-height: 52px;\">\r\n<h2 style=\"margin: 0px 10px 0px 0px; padding: 6px 5px 0px 0px; border-width: 2px 0px 0px; border-top-style: solid; border-right-style: initial; border-bottom-style: initial; border-left-style: initial; border-top-color: rgb(0, 102, 153); border-right-color: initial; border-bottom-color: initial; border-left-color: initial; border-image: initial; font-size: 20px; vertical-align: baseline; font-weight: 400; float: left; position: relative; top: -2px; line-height: 26px; background: url(&quot;//gss0.bdstatic.com/5foIcy0a2gI2n2jgoY3K/static/fisp_static/guonei/img/mod_coltitle/top_bg_f32315c.png&quot;) right -674px no-repeat; text-align: left; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 黑体; color: rgb(0, 102, 153);\"><span class=\"subtitle\" style=\"margin: 0px; padding: 0px 5px 0px 0px; border: 0px; vertical